  // code置换用户id
  getCode(back){
    let that = this
    wx.login({
      success: res => {
        if (res.code) {
          wx.request({
            url: that.data.url + '/ffkj-main/api/user/auth1',
            data: {
              'nowAppId': that.data.appid,
              'nowCode': res.code
            },
            method: 'get',
            scriptCharset: "utf-8",
            header: {
              'content-type': 'application/json; charset=utf8',
            },
            success: function (json) {
              json = json.data
              if (json.code == 0) {
                that.data.openid = json.data.openId
                that.data.userid = json.data.userId
                that.data.sessionKey = json.data.sessionKey

                wx.setStorageSync('openid', json.data.openId);
                wx.setStorageSync('userid5', json.data.userId);
                wx.setStorageSync('sessionKey', json.data.sessionKey);
                // console.log('user/auth1-----')
                // console.log(json)

                // 是否是新用户
                if (!that.data.isNewUser){
                  that.data.isNewUser = json.data.isNewUser
                }
                
                // 用户已授权信息
                if (json.data.userHasUserInfo) {
                  that.data.userState = 1
                }
                // 用户已经授权手机号
                if (json.data.userHasPhone) {
                  that.data.phoneState = 1
                }
               
                back();
                that.myinfo()
              }
            }
          })
        }
      }
    })
  },


