  // 授权用户信息
  getUserInfo(e,callback) {
    if ( !e.detail.encryptedData ){
      return false
    }
    var that = this;
    var ency = e.detail.encryptedData;
    var iv = e.detail.iv;
    // console.log('userid = '+that.data.userid)
    // console.log('sessionKey = ' + that.data.sessionKey)
    // console.log('encryptedData = ' + ency)
    // console.log('ivStr = ' + iv)
    // console.log('sharerId = ' +that.data.sharerId)
    // console.log('commodityId = ' +that.data.commodityId)
    that.ajax('/ffkj-main/api/user/miniapp/auth2', {
      userId: that.data.userid,
      sessionKey: that.data.sessionKey,
      encryptedData: ency,
      ivStr: iv
    }, 'get', res => {
      // console.log(res)
      that.myinfo()
      if (res.code == 0 ){
        that.data.userState = 1
      }
      callback(res)
    })
  },


  // 授权手机号
  getPhoneNumber(e,callback) {
    var that = this
    var ency = e.detail.encryptedData;
    var iv = e.detail.iv;
    var errMsg = e.detail.errMsg
    if (iv == null || ency == null) {
      wx.showToast({
        title: "为确保能正常访问商城，需要授权您的手机号码，感谢您的配合",
        icon: 'none',
      })
      return false
    }
    that.ajax('/ffkj-main/api/user/miniapp/auth3', {
      userId: that.data.userid,
      sessionKey: that.data.sessionKey,
      encryptedData: ency,
      ivStr: iv
    }, 'get', res => {
      // console.log(res)
      that.myinfo()
      if (res.code == 0) {
        that.data.phoneState = 1
      }
      callback(res)
    })
  },
  

  // ajax封装
  ajax(path, data, method,success, fail, url, notloading) {
    let that = this
    // console.log('userid == ' + this.data.userid)
    if (this.data.userid != '' ){
      if (!notloading){
        wx.showLoading({
          title: '加载中',
        })
      }
      if (url) {
        url = url;
      } 
      else {
        url = this.data.url;
      }
      data.appId = this.data.appid
      // 分享者id
      data.shareId = this.globalData.shareId

      wx.request({
        url: url + path,
        data: data,
        method: method,
        scriptCharset: "utf-8",
        header: { 
          'content-type': 'application/json; charset=utf8',
          'userId': that.data.userid,
          'clientType': '1',
          'beginTime': that.globalData.beginTime,
          'userType': that.globalData.users.userType,
        },
        success: function (res) {
          // console.log('分段------------' )
          // console.log(path)
          // console.log(data)
          // console.log(res.data)
          wx.hideLoading();
          // 用户未授权
          if (res.data.code == 10002){
            that.data.openid = '';
            that.data.userid = '';
            that.data.sessionKey = '';
            wx.setStorageSync('openid', '');
            wx.setStorageSync('userid5', '');
            wx.setStorageSync('sessionKey', '');
            
            wx.showModal({
              title: '系统提示',
              content: '账号已过期，需要重新登录',
              showCancel: false,
              confirmText:'回首页',
              success(res) {
                if (res.confirm) {
                  that.globalData.isShuaxinIndex = true
                  wx.redirectTo({
                    url: '/pages/Other/pages/none/index'
                  })
                } 
                else if (res.cancel) {
                  console.log('用户点击取消')
                }
              }
            })
            return false
          }
          else if (res.data.code == 10000) {
            console.log('未知错误')
            console.log(path)
            console.log(data)
            wx.showToast({
              title: res.data.message + ' -' + res.data.code,
              icon: 'none'
            })
          }
          else{
            success(res.data)
          }
        },
        fail: function (res) {
          Toast({
            message: JSON.stringify(res),
            selector: '#custom-selector'
          })
          fail(res)
        },
      })
    }
  },
  
  // 获取当前页面栈的实例，以数组形式按栈的顺序给出，第一个元素为首页，最后一个元素为当前页面。
  fnGetCurrentPages: function () {
    　　var pages = getCurrentPages();    //获取加载的页面
        // console.log('page-----------------------')
        // console.log(pages)
        // console.log('page-----------------------END') 
        if (pages.length>0){
        　　var currentPage = pages[pages.length - 1];  //获取当前页面的对象
            var url = currentPage.route;  //当前页面url
            // console.log('url = ' + url)
        　　var options = currentPage.options;   //获取url中所带的参数

        　　//拼接url的参数
        　　var currentPage = url + '?';
        　　for (var key in options) {
          　　　　var value = options[key]
          　　　　currentPage += key + '=' + value + '&';
        　　}
        　　currentPage = currentPage.substring(0, currentPage.length - 1);
        　　return currentPage;
        }
        else{
          return '';
        }
  },

  // 时间戳
  timestampToTime(timestamp) {
    let date = new Date(timestamp),//时间戳为10位需*1000，时间戳为13位的话不需乘1000
      Y = date.getFullYear() + '-',
      M = (date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1) + '-',
      D = (date.getDate() < 10 ? '0' + date.getDate() : date.getDate()) + ' ',
      h = (date.getHours() < 10 ? '0' + date.getHours() : date.getHours()) + ':',
      m = (date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()) + ':',
      s = date.getSeconds() < 10 ? '0' + date.getSeconds() : date.getSeconds();
    return Y + M + D + h + m + s;
  },
  
